function App() {

  const searchMovie = async (title) => {
    const response = await fetch(`${API_URL}&s=${title}`);
    const data = await response.json();

    console.log(data.Search)
  }

  useEffect(() => {
    searchMovie('Spiderman');
  }, []);

  return (
    <div className="App">
      <h1>MoviesLand</h1>
      <div className="search">
        <input 
          placeholder="search for a movie..."
        />
      </div>
    </div>
  );
}

export default App;
